mapper.select:
select *
from player
where 1=1

	and id = #{id}


	and phone = #{phone}


	and email = #{email}


	and nickname = #{nickname}


	and username = #{username}


	and userpwd = #{userpwd}

select count(1)
from player

	
		and id = #{id}
	
	
		and phone = #{phone}
	
	
		and password = #{password}
	
	
		and username = #{username}
	
	
		and userpwd = #{userpwd}
	
	
		and nickname = #{nickname}

mapper.update:
update player set
userpwd=#{userpwd}
where id =#{id} or
phone=#{phone}

