mapper.select:
select *
from user
where 1=1

	and id = #{id}


	and phone like #{phone}


	and status = #{status}


	and usertype = #{usertype}

select count(1)
from user where 1=1

	and id = #{id}


	and phone like #{phone}


	and password = #{password}


	and status = #{status}


	and usertype = #{usertype}

mapper.update:
update user set
status=#{status}
where
id=#{id}

update user set
password=#{password}
where id =#{id} or
phone=#{phone}

